function pay_weixin(json){var appId=json.data.result.appId;var timeStamp=json.data.result.timeStamp;var signType=json.data.result.signType;var paySign=json.data.result.paySign;var package=json.data.result.package;var nonceStr=json.data.result.nonceStr;var wechatInfo=navigator.userAgent.match(/MicroMessenger\/([\d\.]+)/i);if(wechatInfo[1]<"5.0"){alert('需要微信版本5.0以上才能使用微信支付');return false}else{WeixinJSBridge.invoke('getBrandWCPayRequest',{appId:''+appId+'',timeStamp:''+timeStamp+'',nonceStr:''+nonceStr+'',package:''+package+'',signType:''+signType+'',paySign:''+paySign+'',},function(res){WeixinJSBridge.log(res.err_msg);if(res.err_msg=="get_brand_wcpay_request:ok"){alert("支付成功");window.location.href='http://m.30mifi.com/slhd/mika/gmList.html?userId='+userId+'&tId=1'}else{alert("支付失败")}})}}